mapper.update:
delete from sys_role_menu


		and role_id = #{roleId}


		and menu_id = #{menuId}

mapper.select:
select distinct t.* from sys_menu t inner join sys_role_menu r on r.menu_id = t.id where r.role_id in
#{roleId}
and t.type = #{type}
ORDER BY sort ASC

select distinct t.* from sys_menu t inner join sys_role_menu r on r.menu_id = t.id inner join sys_role rl on rl.id = r.role_id where rl.code in
#{roleCode}
and t.type = #{type}
and t.hidden = 0 ORDER BY sort ASC
